import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';

export default class News extends Component {
   
  
  constructor(){
    super();
    console.log("Hello I am a contructor from News Component");
    this.state = {
      articles :[] ,//this a variable of that upper articles... this show that hum kaise ek state jaise constructor mai ek state jaise article ko show kr skte hai add krke
      loading:false,
      page:1
    }
  }
 // ye async component aisa fn hai ye jb render hoga after rendering the constructor fn  to resolve some promises
 
  async componentDidMount() {
   
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=8a961fe35b374404a3ee5adb75e14e4b&page=1&pageSize=${this.props.pageSize}`;
    let data = await fetch(url);
    let parsedData = await data.json()
    console.log(parsedData);
    this.setState({articles:parsedData.articles, totalResults: parsedData.totalResults})
  }

  handlePrevClick =async()=> {
    console.log("Previous");

    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=8a961fe35b374404a3ee5adb75e14e4b&page=${this.state.page -1}&pageSize=${this.props.pageSize}`;
     this.setState({loading:true});
    let data = await fetch(url);
    let parsedData = await data.json()
    console.log(parsedData);
    this.setState({articles:parsedData.articles})
    this.setState({
      page: this.state.page -1,
      articles:parsedData.articles,
      loading:false
    })
  }

  handleNextClick = async()=> {
    console.log("Next");
    if (!(this.state.page +1 > Math.ceil(this.state.totalResults/this.props.pageSize))) {

    
    
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=8a961fe35b374404a3ee5adb75e14e4b&page=${this.state.page +1}&pageSize=${this.props.pageSize}`;
    this.setState({loading:true});
    let data = await fetch(url);
    let parsedData = await data.json()
    
    this.setState({articles:parsedData.articles})
    this.setState({
      page: this.state.page +1,
      articles:parsedData.articles,
      loading:false
      }  )
    
  }
}
    render() {
    console.log("render")
    return (
      <div className= "container my-3">
        <h1 className="text-center"> NewsMonkey - Top Headlines</h1>
        {this.state.loading && <Spinner/>}
       <div className='row'>
      
        {this.state.articles.map((element)=> {
          return  <div className='col-md-4'  key={element.url}>
          <NewsItem title={element.title?element.title.slice(0, 45):''}  description={element.description?element.description.slice(0, 85):''} imageUrl={element.urlToImage} newsUrl={element.url}/>
          </div>
        })}
       
      {/* line 79-83 kya kr rha sbhii news ko ek sath render kr rha */}
  

      </div>
      <div className='container d-flex justify-content-between'>
        <button disabled={this.state.page<=1} type="button" className="btn btn-primary" onClick={this.handlePrevClick}> &larr;Previous</button>
        <button disabled= {this.state.page +1 > Math.ceil(this.state.totalResults/this.props.pageSize)}type="button" className="btn btn-primary" onClick={this.handleNextClick}> Next &rarr;</button>  
            </div>
      </div>
      
    )
  }
}
