    super();
    console.log("Hello I am a contructor from News Component");
    this.state = {
      articles :this.articles  //this a variable of that upper articles... this show that hum kaise ek state jaise constructor mai ek state jaise article ko show kr skte hai add krke
      
    }
  }
  
  render() {
    
    return (
      <div className= "container my-3">
       <h2>NewsMonkey - Top Headlines</h2>
       <div className='row'>
      
        {this.state.articles.map((element)=> {
          return  <div className='col-md-4'  key={element.url}>
          <NewsItem title={element.title}  description={element.description} imageUrl={element.urlToImage} newsUrl={element.url}/>
          </div>
        })}
       
      {/* line 79-83 kya kr rha sbhii news ko ek sath render kr rha */}
  

      </div>
      </div>
    )
  }
}
